$("#formCadastro").on('submit', function (event){
    event.preventDefault();
    let dados = $(this).serialize();

    $.ajax({
        url: 'controller/ControllerCadastro.php',
        type: 'post',
        dataType: 'html',
        data: dados,
        success: function(dados) {
            $('.resultado').show().html(dados);
        }
    })

});